mapper.select:
SELECT * FROM ( SELECT ntm.SEQ , ntm.CLIENT_ID , ntm.APP_GUBUN , ntm.TEMPLATE_CODE , ntm.HEADER , ntm.STATUS , ntm.REJECT_REASON , ntm.EXAMPLE1 , ntm.EXAMPLE2 , ntm.EXAMPLE3 , ntm.EXAMPLE4 , ntm.EXAMPLE5 , ntm.REGI_DATE, ntm.REPORT_DATE, ntm.DONE_DATE, ntm.BUTTON_NAME , ntm.IS_ACTIVATED, nu.CLIENT_NAME , nu.ORG_NAME , nu.GROUP_NAME , nu.ORG_DEPT, bs.KAKAO_CHANNEL_ID , bs.KAKAO_KEY , bs.NAVER_CHANNEL_ID , bs.NAVER_KEY , ROW_NUMBER() OVER (ORDER BY ntm.SEQ DESC) AS RN, ROW_NUMBER() OVER (ORDER BY ntm.SEQ ) AS ROW_NUM FROM MGOV.NM_TEMPLATECODE_MNG ntm LEFT JOIN MGOV.NM_USER nu ON ntm.CLIENT_ID = nu.CLIENT_ID LEFT JOIN MGOV.BIZM_SENDERKEY bs ON nu.CLIENT_ID = bs.CLIENT_ID
ntm.SEQ = #{seq}
AND ( ntm.CLIENT_ID = #{clientId} OR nu.MASTER_ID = #{clientId} )
AND bs.KAKAO_CHANNEL_ID LIKE '%' || #{kakaoChannelId} || '%'
AND ntm.APP_GUBUN = #{appGubun}
AND nu.ORG_NAME LIKE '%' || #{orgName} || '%'
AND nu.ORG_DEPT LIKE '%' || #{orgDept} || '%'
AND nu.GROUP_NAME LIKE '%' || #{groupName} || '%'
AND ntm.TEMPLATE_CODE LIKE '%' || #{templateCode} || '%'
AND ntm.STATUS IN
#{item}
AND ntm.IS_ACTIVATED = #{isActivated}
AND bs.KAKAO_KEY IS NOT NULL
AND bs.KAKAO_KEY IS NULL
) WHERE RN >= #{start} AND RN <= #{end}

SELECT ntm.SEQ , ntm.CLIENT_ID , ntm.APP_GUBUN , ntm.TEMPLATE_CODE , ntm.HEADER , ntm.STATUS , ntm.REJECT_REASON , ntm.EXAMPLE1 , ntm.EXAMPLE2 , ntm.EXAMPLE3 , ntm.EXAMPLE4 , ntm.EXAMPLE5 , ntm.REGI_DATE, ntm.BUTTON_NAME , ntm.USE_HTTPS, ntm.REPORT_DATE, ntm.IS_ACTIVATED, nu.CLIENT_NAME , nu.ORG_NAME , nu.GROUP_NAME , nu.ORG_DEPT , bs.KAKAO_CHANNEL_ID , bs.KAKAO_KEY , bs.NAVER_CHANNEL_ID , bs.NAVER_KEY FROM MGOV.NM_TEMPLATECODE_MNG ntm LEFT JOIN MGOV.NM_USER nu ON ntm.CLIENT_ID = nu.CLIENT_ID LEFT JOIN MGOV.BIZM_SENDERKEY bs ON nu.CLIENT_ID = bs.CLIENT_ID
ntm.SEQ = #{seq}
AND ( ntm.CLIENT_ID = #{clientId} OR nu.MASTER_ID = #{clientId} )
AND ntm.APP_GUBUN = #{appGubun}
AND nu.ORG_NAME LIKE '%' || #{orgName} || '%'
AND nu.ORG_DEPT LIKE '%' || #{orgDept} || '%'
AND nu.GROUP_NAME LIKE '%' || #{groupName} || '%'
AND ntm.TEMPLATE_CODE LIKE '%' || #{templateCode} || '%'
AND ntm.STATUS IN
#{item}

SELECT * FROM ( SELECT NTM.SEQ , NTM.CLIENT_ID , NTM.APP_GUBUN , NTM.TEMPLATE_CODE , NTM.HEADER , NTM.STATUS , NTM.REJECT_REASON , NTM.EXAMPLE1 , NTM.EXAMPLE2 , NTM.EXAMPLE3 , NTM.EXAMPLE4 , NTM.EXAMPLE5 , NTM.REGI_DATE , NTM.REPORT_DATE , NTM.BUTTON_NAME , NTM.IS_ACTIVATED , ROW_NUMBER() OVER (ORDER BY NTM.REGI_DATE DESC) AS RN FROM MGOV.NM_TEMPLATECODE_MNG NTM, MGOV.NM_USER NU
NTM.CLIENT_ID = NU.CLIENT_ID(+)
AND NTM.SEQ = #{seq}
AND NTM.CLIENT_ID = #{clientId}
AND NTM.APP_GUBUN = #{appGubun}
AND NTM.TEMPLATE_CODE LIKE '%' || #{templateCode} || '%'
AND NTM.HEADER LIKE '%' || #{header} || '%'
AND NTM.STATUS = #{status}
AND NU.UP_ORG_ID = #{orgId}
AND NTM.IS_ACTIVATED = #{isActivated}
) WHERE RN >= #{start} AND RN <= #{end}

SELECT SEQ , CLIENT_ID , APP_GUBUN , TEMPLATE_CODE , HEADER , STATUS , REJECT_REASON , EXAMPLE1 , EXAMPLE2 , EXAMPLE3 , EXAMPLE4 , EXAMPLE5 , REGI_DATE , REPORT_DATE , BUTTON_NAME , USE_HTTPS , ROW_NUMBER() OVER (ORDER BY SEQ DESC) AS RN FROM MGOV.NM_TEMPLATECODE_MNG
AND SEQ = #{seq}
AND CLIENT_ID = #{clientId}
AND APP_GUBUN = #{appGubun}
AND TEMPLATE_CODE LIKE '%' || #{templateCode} || '%'
AND HEADER LIKE '%' || #{header} || '%'
AND STATUS = #{status}

SELECT COUNT(*)
FROM MGOV.NM_TEMPLATECODE_MNG
WHERE CLIENT_ID = #{clientId} AND TEMPLATE_CODE = #{templateCode}

SELECT 
	COUNT(*)
FROM MGOV.NM_TEMPLATECODE_MNG NTM, MGOV.NM_USER NU

	NTM.CLIENT_ID = NU.CLIENT_ID(+) 

		AND NTM.SEQ = #{seq}


		AND NTM.CLIENT_ID = #{clientId}


		AND NTM.APP_GUBUN = #{appGubun}


		AND NTM.TEMPLATE_CODE LIKE '%' || #{templateCode} || '%'


		AND NTM.HEADER LIKE '%' || #{header} || '%'


		AND NTM.STATUS = #{status}


		AND NU.UP_ORG_ID = #{orgId}


		AND NTM.IS_ACTIVATED = #{isActivated}

SELECT
	*
FROM
	MGOV.NM_TEMPLATECODE_MNG


		AND CLIENT_ID = #{clientId}


		AND CLIENT_ID IN ( SELECT CLIENT_ID FROM MGOV.NM_USER WHERE UP_ORG_ID = #{clientId} )


		AND STATUS = #{status}


		AND TEMPLATE_CODE = #{templateCode}


		AND HEADER = #{header}

SELECT * FROM ( SELECT A.*, ROWNUM AS RN FROM ( SELECT t.SEQ , t.CLIENT_ID , t.APP_GUBUN , t.TEMPLATE_CODE , t.HEADER , t.STATUS , t.EXAMPLE1 , t.EXAMPLE2 , t.EXAMPLE3 , t.EXAMPLE4 , t.EXAMPLE5 , TO_CHAR(t.REGI_DATE, 'YYYY-MM-DD HH24:MI:SS') AS REGI_DATE , TO_CHAR(t.REPORT_DATE, 'YYYY-MM-DD HH24:MI:SS') AS REPORT_DATE , t.BUTTON_NAME , t.USE_HTTPS , t.REJECT_REASON , s.KAKAO_CHANNEL_ID , s.KAKAO_KEY , s.NAVER_CHANNEL_ID , s.NAVER_KEY FROM MGOV.NM_TEMPLATECODE_MNG t , MGOV.BIZM_SENDERKEY s WHERE t.CLIENT_ID = s.CLIENT_ID
AND t.CLIENT_ID = #{clientId}
AND t.APP_GUBUN = #{appGubun}
AND t.TEMPLATE_CODE = #{templateCode}
AND t.HEADER LIKE '%' || #{header} || '%'
AND t.STATUS = #{status}
AND t.KAKAO_CHANNEL_ID = #{kakaoChannelId}
AND t.KAKAO_KEY = #{kakaoKey}
AND t.NAVER_CHANNEL_ID = #{naverChannelId}
AND t.NAVER_KEY = #{naverKey}
t.SEQ ASC
t.CLIENT_ID ASC
t.TEMPLATE_CODE ASC
t.HEADER ASC
t.KAKAO_CHANNEL_ID ASC
t.HEADER ASC
t.REPORT_DATE ASC
t.SEQ DESC
t.CLIENT_ID DESC
t.TEMPLATE_CODE DESC
t.HEADER DESC
t.KAKAO_CHANNEL_ID DESC
t.HEADER DESC
t.REPORT_DATE DESC
) A ) WHERE RN BETWEEN #{startRowNum} AND #{endRowNum}

SELECT
	COUNT(*)
FROM
    MGOV.NM_TEMPLATECODE_MNG t
    , MGOV.BIZM_SENDERKEY s
WHERE
    t.CLIENT_ID = s.CLIENT_ID

    	AND t.CLIENT_ID = #{clientId}


    	AND t.APP_GUBUN = #{appGubun}


    	AND t.TEMPLATE_CODE = #{templateCode}


    	AND t.HEADER LIKE '%' || #{header} || '%'


    	AND t.STATUS = #{status}


    	AND t.KAKAO_CHANNEL_ID = #{kakaoChannelId}


    	AND t.KAKAO_KEY = #{kakaoKey}


    	AND t.NAVER_CHANNEL_ID = #{naverChannelId}


    	AND t.NAVER_KEY = #{naverKey}

mapper.update:
